mapper.select:
select 'true' as QUERYID,

from oss_alarm_DailyLost
where AccountDate = #{accountdate,jdbcType=TIMESTAMP}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.delete:
delete from oss_alarm_DailyLost
where AccountDate = #{accountdate,jdbcType=TIMESTAMP}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.select:
select * from oss_alarm_DailyLost where 1=1

   AND AccountDate>=#{begin,jdbcType=TIMESTAMP}


   AND AccountDate < #{end,jdbcType=TIMESTAMP}


    and OUCode LIKE #{oucode,jdbcType=VARCHAR}

ORDER BY AccountDate DESC

select * from oss_alarm_DailyLost where  1=1

   AND AccountDate>=#{begin,jdbcType=TIMESTAMP}


   AND AccountDate < #{end,jdbcType=TIMESTAMP}


    and OUCode LIKE #{oucode,jdbcType=VARCHAR}

ORDER BY AccountDate DESC

   LIMIT #{firstRow},#{pageSize}
