mapper.select:
SELECT * FROM oss_alarm_ShiftLost WHERE 1=1

 AND  LEFT(Shift,8)>=#{start,jdbcType=VARCHAR}


 And LEFT(Shift,8)<=#{end,jdbcType=VARCHAR}


    and OUCode LIKE #{oucode,jdbcType=VARCHAR}

ORDER BY Shift DESC

SELECT * FROM oss_alarm_ShiftLost WHERE 1=1

   AND  LEFT(Shift,8)>=#{start,jdbcType=VARCHAR}


   And LEFT(Shift,8)<=#{end,jdbcType=VARCHAR}


    and OUCode LIKE #{oucode,jdbcType=VARCHAR}

  ORDER BY Shift DESC

   LIMIT #{firstRow},#{pageSize}

select OUCode,LEFT(ShortName,2) as ShortName,COUNT(*) as num from ( select (SELECT ShortName from oss_sys_OrgUnit where oss_sys_OrgUnit.OUCode=LEFT(oss_alarm_DailyLost.OUCode,6)) as ShortName,REPLACE(substring(AccountDate,1,10),'-','') as Shift,oss_alarm_DailyLost.OUCode from oss_alarm_DailyLost UNION ALL select (SELECT ShortName from oss_sys_OrgUnit where oss_sys_OrgUnit.OUCode=LEFT(oss_alarm_ShiftLost.OUCode,6)) as ShortName,LEFT(Shift,8) as Shift,oss_alarm_ShiftLost.OUCode from oss_alarm_ShiftLost UNION ALL select (SELECT ShortName from oss_sys_OrgUnit where oss_sys_OrgUnit.OUCode=LEFT(oss_alarm_Equipment.OUCode,6)) as ShortName,LEFT(Shift,8) as Shift,oss_alarm_Equipment.OUCode from oss_alarm_Equipment UNION ALL select (SELECT ShortName from oss_sys_OrgUnit where oss_sys_OrgUnit.OUCode=LEFT(oss_alarm_SaleOut.OUCode,6)) as ShortName,LEFT(Shift,8) as Shift,oss_alarm_SaleOut.OUCode from oss_alarm_SaleOut UNION ALL select (SELECT ShortName from oss_sys_OrgUnit where oss_sys_OrgUnit.OUCode=LEFT(oss_alarm_Inventory.OUCode,6)) as ShortName,LEFT(Shift,8) as Shift,oss_alarm_Inventory.OUCode from oss_alarm_Inventory ) a where 1=1
AND Shift = #{start,jdbcType=VARCHAR}
and OUCode LIKE #{oucode,jdbcType=VARCHAR}
GROUP BY LEFT(OUCode,6) ORDER BY num DESC

select 'true' as QUERYID,

from oss_alarm_ShiftLost
where Shift = #{shift,jdbcType=VARCHAR}
  and OilCanNo = #{oilcanno,jdbcType=INTEGER}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.delete:
delete from oss_alarm_ShiftLost
where Shift = #{shift,jdbcType=VARCHAR}
  and OilCanNo = #{oilcanno,jdbcType=INTEGER}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}
