mapper.select:
select 'true' as QUERYID,

from oss_daily_DailyBalance
where AccountDate = #{accountdate,jdbcType=TIMESTAMP}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.delete:
delete from oss_daily_DailyBalance
where AccountDate = #{accountdate,jdbcType=TIMESTAMP}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM oss_daily_DailyBalance WHERE AccountDate>=#{start,jdbcType=TIMESTAMP} And AccountDate<#{end,jdbcType=TIMESTAMP}
and OUCode LIKE #{oucode,jdbcType=VARCHAR}

   LIMIT #{firstRow},#{pageSize}
