mapper.select:
select 'true' as QUERYID,

from oss_daily_opoCount
where Shift = #{shift,jdbcType=VARCHAR}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.delete:
delete from oss_daily_opoCount
where Shift = #{shift,jdbcType=VARCHAR}
  and OilNo = #{oilno,jdbcType=VARCHAR}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}
