mapper.select:
select 'true' as QUERYID,

from oss_sysmanage_AlarmParameter
where OilCan = #{oilcan,jdbcType=INTEGER}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}

mapper.delete:
delete from oss_sysmanage_AlarmParameter
where OilCan = #{oilcan,jdbcType=INTEGER}
  and NodeNo = #{nodeno,jdbcType=VARCHAR}
