#{oil,jdbcType=DOUBLE}, #{standardl,jdbcType=DOUBLE}, #{heighttotal,jdbcType=DOUBLE},
#{waterheight,jdbcType=DOUBLE}, #{waterl,jdbcType=DOUBLE}, #{temperature,jdbcType=DOUBLE}
