mapper.select:
select 'true' as QUERYID,

from oss_monitor_TimeInventory
where OilCan = #{oilcan,jdbcType=INTEGER}
  and StoreTime = #{storetime,jdbcType=TIMESTAMP}

mapper.delete:
delete from oss_monitor_TimeInventory
where OilCan = #{oilcan,jdbcType=INTEGER}
  and StoreTime = #{storetime,jdbcType=TIMESTAMP}
